import React from 'react';
import styles from './StartScreen.module.css';
import { startFlagQuiz } from '../modes/FlagQuiz.js';

const StartScreen = ({ onStart }) => {
  return (
    <div className={styles.card}>
      <h1 className={styles.title}>Flag Quiz</h1>
      <button className={styles.buttonPrimary} onClick={startFlagQuiz}>Guess the Flag</button>
      <button className={styles.buttonPrimary} onClick={startCapitalQuiz}>Guess the Capital</button>
    </div>
  );
};

export default StartScreen;
