import React from 'react';
import styles from './QuizContainer.module.css';
import FlagImage from './FlagImage'; 
import ProgressBar from './ProgressBar';

function QuizContainer({ mode, current, questionIdx, quiz }) {
  if (mode === "flag") {
    return (
      <div className={`${styles.card} ${styles.quizContainer}`}>
        <p className="progress-text">...</p>
        <h2 className={styles.question}>
          Guess the flag of <span className={styles.questionHighlight}>{current.name.common}</span>
        </h2>
      </div>
    );
  }
  else if (mode === "capital") {
    return (
      <div className={`${styles.card} ${styles.quizContainer}`}>
        <ProgressBar current={questionIdx} total={quiz.length} />
        <h2 className={styles.question}>
          Guess the capital of <span className={styles.questionHighlight}>{current.name.common}</span>
        </h2>
      </div>
    );
  }
}

export default QuizContainer;

