import React, { useState } from "react";
import './App.css';
import './components/global.css';
import StartScreen from './components/StartScreen';
import QuizContainer from './components/QuizContainer'; // Changed from QuizCard
import ResultScreen from './components/ResultScreen'; // Changed from FinishScreen
import useCountries from './hooks/useCountries';
import shuffle from "./utils/shuffle";
import generateOptions from "./utils/generateOptions";
import Loading from './components/Loading';
import ErrorScreen from './components/ErrorScreen'; // New component for error handling


function App() {
  //initialize state variables
  const [quiz, setQuiz] = useState([]);
  const [answer, setAnswer] = useState(null);
  const [score, setScore] = useState(0);
  const [questionIdx, setQuestionIdx] = useState(0);
  const [started, setStarted] = useState(false);
  const [finished, setFinished] = useState(false);
  const [options, setOptions] = useState([]);
  const { countries, loading, error } = useCountries();
  const [mode, setMode] = useState(null);


  //determine the type of quiz, and pass on all info
  function setupQuiz() {
    const shuffled = shuffle(countries);
    const tenQuestions = shuffled.slice(0, 10);
    gamemode

    const firstCapital = tenQuestions[0]?.capital?.[0] || "";

    setQuiz(tenQuestions);
    setAnswer(firstCapital);
    setOptions(generateOptions(firstCapital, countries));
    setQuestionIdx(0);
    setScore(0);
    setStarted(true);
    setFinished(false);
  }

  function handleAnswer(selected) {
    document.activeElement.blur();

    for (let i = 0; i < 4; i++) {
      const optionButton = document.getElementById(`option-${i}`);
      const buttonText = optionButton.textContent;
      if (buttonText === answer) {
        optionButton.classList.add("correct");
      } else {
        optionButton.classList.add("wrong");
      }
    }

    if (selected === answer) {
      setScore((prev) => prev + 1);
      const liveScore = document.querySelector('.live-score');
      const scoreValue = document.querySelector('.score-value');
      if (liveScore) {
        liveScore.classList.add('celebrate');
        setTimeout(() => liveScore.classList.remove('celebrate'), 600);
      }
      if (scoreValue) {
        scoreValue.classList.add('update');
        setTimeout(() => scoreValue.classList.remove('update'), 600);
      }
    }

    setTimeout(() => {
      if (questionIdx < quiz.length - 1) {
        const nextIndex = questionIdx + 1;
        const nextCapital = quiz[nextIndex]?.capital?.[0] || "";
        setQuestionIdx(nextIndex);
        setAnswer(nextCapital);
        setOptions(generateOptions(nextCapital, countries));
      } else {
        setFinished(true);
      }
      for (let i = 0; i < 4; i++) {
        const optionButton = document.getElementById(`option-${i}`);
        optionButton.classList.remove("correct", "wrong");
      }
    }, 1200);
  }

  return (
    <div className="app-container">
      <div className="card-container">
        {loading && <Loading />}
        {error && <ErrorScreen error={error} />}
        {!started && !loading && !error && <StartScreen setMode={setMode} onStart={setupQuiz} />}
        {started && !finished && (
          <QuizContainer
            quiz={quiz}
            questionIdx={questionIdx}
            answer={answer}
            options={options}
            score={score}
            onAnswer={handleAnswer}
          />
        )}
        {finished && (
          <ResultScreen
            score={score}
            total={quiz.length}
            onRestart={setupQuiz}
          />
        )}
      </div>
    </div>
  );
}

export default App;
