import React from 'react';
import styles from './OptionsGrid.module.css';

const OptionsGrid = ({ options, onOptionClick, correctAnswer, selectedAnswer }) => {
  return (
    <div className={styles.optionsGrid}>
      {options.map((option, index) => {
        let className = styles.optionButton;
        if (selectedAnswer) {
          if (option.textContent === correctAnswer) className += ` ${styles.correct}`;
          else if (option.textContent === selectedAnswer) className += ` ${styles.wrong}`;
        }
        return (
          <button
            key={index}
            className={className}
            onClick={() => onOptionClick(option)}
            disabled={!!selectedAnswer}
          >
            {option}
          </button>
        );
      })}
    </div>
  );
};

export default OptionsGrid;
