import React from 'react';
import './StartScreen.module.css';

function StartScreen({ onStart }) {
  return (
    <div className="bubble-card">
      <h2 className="bubble-title">Geography Trivia Quiz</h2>
      <p className="intro-text">Test your knowledge of world geography!</p>
      
      <div className="button-container">
        <button 
          className="bubble-button"
          onClick={() => onStart('capitals')}
        >
          Guess the Capital
        </button>
        
        <button 
          className="bubble-button"
          onClick={() => onStart('flags')}
        >
          Guess the Flag
        </button>
        
        <button 
          className="bubble-button"
          onClick={() => onStart('countries')}
        >
          Guess the Country
        </button>
      </div>
    </div>
  );
}

export default StartScreen;
