import './App.css';
import React, { useState, useEffect } from "react";

//#################################################################################
//preliminaries for handling and updating data
const [quiz, setQuiz] = useState(null); //to set up the quiz (choose 10 different contries)
const [countries, setCountries] = useState([]);  //put all the countries in array
const [options, setOptions] = useState([]); //to give options for a question
const [answer, setAnswer] = useState(null); // to store answer
const [score, setScore] = useState(0); // to keep track of score
const [question, setQuestion] = useState(0); //keep track of question index
const [started, setStarted] = useState(false); //keep track if quiz statred and finished
const [finished, setfinished] = useState(false); 
const [loading, setLoading] = useState(true);  //show loading
const [error, setError] = useState(null);  //in case theres errors

 useEffect(() => {
  fetch("https://restcountries.com/v3.1/all?fields=name,capital,flags")
    .then((res) => res.json())
    .then((data) => {
      setCountries(data.results);  //
    })
    .catch((err) => console.error(err));
}, []);
//################################################################################



//shuffles the array of countries so it is different each time
function shuffle(){
  let arr=countries.slice();
  for(let i = arr.length -1; i>0; i--){
    const j = Math.floor(Math.random()*(i+1));
    [arr[i],arr[j]] = [arr[j],arr[i]];
  }
}



function App() {
  return (
    <div className="App">

    </div>
  );
}

export default App;
