import React, { useState, useEffect } from "react";

function App() {
  const [quiz, setQuiz] = useState([]);
  const [countries, setCountries] = useState([]);
  const [answer, setAnswer] = useState(null);
  const [score, setScore] = useState(0);
  const [questionIdx, setQuestionIdx] = useState(0);
  const [started, setStarted] = useState(false);
  const [finished, setFinished] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const[options, setOptions] = useState([]);
  const [feedback, setFeedback] = useState(null); // "correct" or "wrong"


  useEffect(() => {
    fetch("https://restcountries.com/v3.1/all?fields=name,capital,flags")
      .then((res) => res.json())
      .then((data) => {
        const filtered = data.filter(c => c.capital && c.capital[0] && c.flags?.png);
        setCountries(filtered);
        setLoading(false);
      })
      .catch((err) => {
        setError("Failed to fetch countries");
        setLoading(false);
      });
  }, []);

  // Shuffle helper
  function shuffle(arr) {
    const array = arr.slice();
    for (let i = array.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [array[i], array[j]] = [array[j], array[i]];
    }
    return array;
  }

  function setupQuiz() {
    const shuffled = shuffle(countries);
    const tenQuestions = shuffled.slice(0, 10);
    const firstCapital = tenQuestions[0]?.capital?.[0] || "";

    setQuiz(tenQuestions);
    setAnswer(firstCapital);
    setOptions(generateOptions(firstCapital, countries));
    setQuestionIdx(0);
    setScore(0);
    setStarted(true);
    setFinished(false);
  }


  function renderQuestion() {
    if (!quiz.length) return null;
    const current = quiz[questionIdx];

    return (
      <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-blue-50 to-blue-100 px-4 py-8 text-center transition-all duration-500 ease-in-out">

        {/* Progress Bar */}
        <div className="w-full max-w-xl bg-gray-300 rounded-full h-4 mb-6">
          <div
            className="bg-green-500 h-4 rounded-full transition-all duration-500"
            style={{ width: `${((questionIdx + 1) / quiz.length) * 100}%` }}
          ></div>
        </div>

        {/* Progress Text */}
        <p className="text-gray-700 text-sm mb-4 font-medium">
          Question {questionIdx + 1} of {quiz.length}
        </p>

        {/* Flag Centered */}
        <div className="mb-6">
          <img
            src={current.flags.png}
            alt={`Flag of ${current.name.common}`}
            className="w-52 h-36 object-cover rounded-lg shadow-lg border-2 border-white mx-auto"
          />
        </div>

        {/* Question */}
        <h2 className="text-2xl sm:text-3xl font-semibold mb-6 text-gray-800">
          What is the capital of{" "}
          <span className="text-blue-600">{current.name.common}</span>?
        </h2>

        {/* Options Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 w-full max-w-md">
          {options.map((option, idx) => (
            <button
              key={idx}
              onClick={() => handleAnswer(option)}
              className="bg-white text-blue-600 font-semibold py-3 px-5 rounded-lg shadow hover:bg-blue-50 hover:shadow-lg focus:outline-none focus:ring-2 focus:ring-blue-400 focus:ring-opacity-75 transition-all duration-150 ease-in-out transform hover:-translate-y-0.5 active:translate-y-0 border border-gray-300 hover:border-blue-400"
            >
              {option}
            </button>
          ))}
        </div>

        {/* Feedback */}
        {feedback && (
          <p
            className={`mt-6 text-xl font-bold transition-opacity duration-300 ${
              feedback === "correct" ? "text-green-600" : "text-red-600"
            }`}
          >
            {feedback === "correct" ? "✅ Correct!" : "❌ Wrong!"}
          </p>
        )}
      </div>
    );
  }



  function handleAnswer(selected) {
    if (selected === answer) {
      setScore((prev) => prev + 1);
      setFeedback("correct");
    } else {
      setFeedback("wrong");
    }

    // Move to next question after delay
    setTimeout(() => {
      if (questionIdx < 9) {
        const nextIndex = questionIdx + 1;
        const nextCapital = quiz[nextIndex]?.capital?.[0] || "";

        setQuestionIdx(nextIndex);
        setAnswer(nextCapital);
        setOptions(generateOptions(nextCapital, countries));
        setFeedback(null); // reset
      } else {
        setFinished(true);
      }
    }, 1000);
  }



  function generateOptions(correctCapital, allCountries) {
    // Filter countries that have valid capitals and are NOT the correct answer
    const validWrongChoices = allCountries
      .filter(c => c.capital && c.capital[0] && c.capital[0] !== correctCapital)
      .map(c => c.capital[0]);

    // Shuffle and take first 3 incorrect options
    const incorrect = shuffle(validWrongChoices).slice(0, 3);

    return shuffle([...incorrect, correctCapital]);
  }

  return (
    <div className="App flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-blue-50 to-blue-100 px-4 py-8 text-center">
      {loading && (
        <div className="text-xl font-semibold text-blue-600">
          <p>Loading countries...</p>
        </div>
      )}

      {error && (
        <div className="text-xl font-semibold text-red-600">
          <p>{error}</p>
        </div>
      )}

      {!started && !loading && !error && (
        <div className="flex flex-col items-center">
          <h1 className="text-3xl font-bold text-blue-600 mb-8">Geography Trivia Quiz</h1>
          <button 
            onClick={setupQuiz}
            className="bg-blue-600 text-white font-semibold py-4 px-8 rounded-lg shadow-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-150 text-xl"
          >
            Start Quiz
          </button>
        </div>
      )}

      {started && !finished && renderQuestion()}

      {finished && (
        <div className="flex flex-col items-center bg-white p-8 rounded-lg shadow-lg">
          <h2 className="text-3xl font-bold text-blue-600 mb-4">Quiz Finished!</h2>
          <p className="text-2xl mb-6">Your score: <span className="font-bold">{score}</span> / {quiz.length}</p>
          <button 
            onClick={setupQuiz}
            className="bg-blue-600 text-white font-semibold py-3 px-6 rounded-lg shadow-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-150"
          >
            Restart Quiz
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
