import React from 'react';
import styles from './OptionsGrid.module.css';

const OptionsGrid = ({ options, onOptionClick, correctAnswer, selectedAnswer, mode}) => {
  if (mode === "capital") {
    return (
      <div className={styles.optionsGrid}>
        {options?.map((option, index) => {
          let className = styles.optionButton;
          if (selectedAnswer) {
            if (option === correctAnswer) className += ` ${styles.correct}`;
            else if (option === selectedAnswer) className += ` ${styles.wrong}`;
          }
          return (
            <button
              id={`option-${index}`}
              key={index}
              className={className}
              onClick={() => onOptionClick(option)}
              disabled={!!selectedAnswer}
            >
              {option}
            </button>
          );
        })}
      </div>
    );
  }  else if (mode === "flag"){
    return (
      <div className={styles.optionsGrid}>
        {options?.map((option, index) => {
          let className = styles.optionButton;
          if (selectedAnswer) {
            if (option === correctAnswer) className += ` ${styles.correct}`;
            else if (option === selectedAnswer) className += ` ${styles.wrong}`;
          }
          return (
            <button
              id={`option-${index}`}
              key={index}
              className={className}
              onClick={() => onOptionClick(option)}
              disabled={!!selectedAnswer}
            >
              <img 
                src={option} 
                alt={`Flag option ${index + 1}`}
                className={styles.flagOptionImage}
                onError={(e) => {
                  console.log(`Failed to load flag: ${option}`);
                  e.target.style.display = 'none';
                  e.target.nextSibling.style.display = 'block';
                }}
                onLoad={(e) => {
                  e.target.style.display = 'block';
                  if (e.target.nextSibling) {
                    e.target.nextSibling.style.display = 'none';
                  }
                }}
              />
              <div 
                className={styles.flagFallback}
                style={{display: 'none'}}
              >
                üè≥Ô∏è Flag {index + 1}
              </div>
            </button>
          );
        })}
      </div>
    );
  }
};

export default OptionsGrid;
