import React from 'react';
import styles from './StartScreen.module.css';

const StartScreen = ({ onStart }, { setMode }) => { 
    const handleStartFlag = () => {
        setMode("flag");
        onStart();
    } 
    const handleStartCapital = () => {
        setMode("capital");
        onStart();
    };
    return (
    <div className={styles.card}>
        <h1 className={styles.title}>Geography Quiz</h1>
        <div className={styles.buttonContainer}>
            <button className={styles.buttonPrimary} onClick={handleStartFlag}>Guess the Flag</button>
            <button className={styles.buttonPrimary} onClick={handleStartCapital}>Guess the Capital</button>
        </div>
    </div>

  );
};

export default StartScreen;
