import React from 'react';
import styles from './OptionsGrid.module.css';

const OptionsGrid = ({ options, onOptionClick, correctAnswer, selectedAnswer, mode}) => {
  
  const handleButtonClick = (option, e) => {
    // Aggressive blur on mobile
    const button = e.currentTarget;
    button.blur();
    
    // Multiple blur attempts with different timings
    setTimeout(() => button.blur(), 0);
    setTimeout(() => button.blur(), 10);
    setTimeout(() => button.blur(), 50);
    setTimeout(() => button.blur(), 100);
    
    // Remove focus from document
    if (document.activeElement) {
      document.activeElement.blur();
    }
    
    onOptionClick(option);
  };

  const handleTouchEnd = (e) => {
    const button = e.currentTarget;
    button.blur();
    setTimeout(() => button.blur(), 0);
    setTimeout(() => button.blur(), 10);
    setTimeout(() => button.blur(), 50);
    
    // Remove any focus from document
    if (document.activeElement && document.activeElement !== document.body) {
      document.activeElement.blur();
    }
  };

  if (mode === "capital") {
    return (
      <div className={styles.optionsGrid}>
        {options?.map((option, index) => {
          let className = styles.optionButton;
          if (selectedAnswer) {
            if (option === correctAnswer) className += ` ${styles.correct}`;
            else if (option === selectedAnswer) className += ` ${styles.wrong}`;
          }
          return (
            <button
              id={`option-${index}`}
              key={index}
              className={className}
              onClick={(e) => handleButtonClick(option, e)}
              onTouchStart={handleTouchEnd}
              onTouchEnd={handleTouchEnd}
              onMouseDown={handleTouchEnd}
              disabled={!!selectedAnswer}
            >
              {option}
            </button>
          );
        })}
      </div>
    );
  } else if (mode === "flag"){
    return (
      <div className={styles.optionsGrid}>
        {options?.map((option, index) => {
          let className = styles.optionButton;
          if (selectedAnswer) {
            if (option === correctAnswer) className += ` ${styles.correct}`;
            else if (option === selectedAnswer) className += ` ${styles.wrong}`;
          }
          return (
            <button
              id={`option-${index}`}
              key={index}
              className={className}
              onClick={(e) => handleButtonClick(option, e)}
              onTouchStart={handleTouchEnd}
              onTouchEnd={handleTouchEnd}
              onMouseDown={handleTouchEnd}
              disabled={!!selectedAnswer}
            >
              <img 
                src={option} 
                alt={`Flag option ${index + 1}`}
                className={styles.flagOptionImage}
              />
            </button>
          );
        })}
      </div>
    );
  }
};

export default OptionsGrid;
