import React from 'react';
import styles from './QuizContainer.module.css';
import FlagImage from './FlagImage'; 
import ProgressBar from './ProgressBar';

function QuizContainer({ mode, current, questionIdx, quiz, answer, options, score }) {
  if (mode === "flag") {
    return (
      <div className={`${styles.card} ${styles.quizContainer}`}>
        <p className="progress-text">...</p>
        <h2 className={styles.question}>
          What is the flag of <span className={styles.questionHighlight}>{current.name.common}</span>
        </h2>
      </div>
    );
  }
  else if (mode === "capital") {
    return (
      <div className={`${styles.card} ${styles.quizContainer}`}>
        `${((questionIdx + 1) / quiz.length) * 100}%`
        <FlagImage src={current.flags.png} alt={`Flag of ${current.name.common}`} />
        <h2 className={styles.question}>
          What is the capital of <span className={styles.questionHighlight}>{current.name.common}</span>
        </h2>
      </div>
    );
  }
}

export default QuizContainer;
