import shuffle from './shuffle';
export default function generateOptions(correctAnswer, allCountries, mode) {
  if (mode === "capital"){
    // Filter countries that have valid capitals and are NOT the correct answer
    const validWrongChoices = allCountries
      .filter(c => c.capital && c.capital[0] && c.capital[0] !== correctAnswer)
      .map(c => c.capital[0]);

    // Shuffle and take first 3 incorrect options
    const incorrect = shuffle(validWrongChoices).slice(0, 3);

    return shuffle([...incorrect, correctCapital]);
  }
  else if (mode === "flag"){
    const validWrongChoices = allCountries
      .filter(c => c.flags && c.flags.png && c.name.common !== correctFlag)
      .map(c => c.flags.png);

      const incorrect = shuffle(validWrongChoices).slice(0, 3);
    return shuffle([...incorrect, correctFlag]);
  }
    
  }
