import React from 'react';
import styles from './ResultScreen.module.css';
// import styles from './ResultScreen.module.css';

// Make sure to destructure props: score, total, onRestart
export default function ResultScreen({ score, total, onRestart }) {
  const percentage = total > 0 ? Math.round((score / total) * 100) : 0;
  let message = "Great job! ðŸŽ‰";

  if (percentage < 30) {
    message = "Keep practicing! You'll get there. ðŸ§";
  } else if (percentage < 60) {
    message = "Not bad! A little more practice needed. ðŸ‘";
  } else if (percentage < 90) {
    message = "Well done! You know your geography! ðŸŒ";
  } else {
    message = "Excellent! You're a geography whiz! ðŸŒŸ";
  }

  return (
    <div className="card"> {/* Use the 'card' class for styling */}
      <h2 className="title">Quiz Completed!</h2>
      <p className="score-text">
        Your score: <span className="score-value">{score}</span> / {total}
      </p>
      <p className="percentage-text">({percentage}%)</p>
      <button
        onClick={onRestart}
        className="button-primary" // Use your primary button style
      >
        Restart Quiz
      </button>
      <p className="result-message">{message}</p>
    </div>
  );
}
